  --shadow-sm: 0px 1px 3px 0px hsl(0 0% 0% / 0.50), 0px 1px 2px -1px hsl(0 0% 0% / 0.50);
  --shadow: 0px 2px 4px -1px hsl(0 0% 0% / 0.50), 0px 4px 6px -1px hsl(0 0% 0% / 0.50);
  --shadow-md: 0px 4px 6px -1px hsl(0 0% 0% / 0.50), 0px 2px 4px -2px hsl(0 0% 0% / 0.40);
  --shadow-lg: 0px 10px 15px -3px hsl(0 0% 0% / 0.50), 0px 4px 6px -4px hsl(0 0% 0% / 0.40);
  --shadow-xl: 0px 20px 25px -5px hsl(0 0% 0% / 0.50), 0px 8px 10px -6px hsl(0 0% 0% / 0.40);
  --shadow-2xl: 0px 25px 50px -12px hsl(0 0% 0% / 0.60);

}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

/**
 * Using the elevate system.
 * Automatic contrast adjustment.
 *
 * <element className="hover-elevate" />
 * <element className="active-elevate-2" />
 *
 * // Using the tailwind utility when a data attribute is "on"
 * <element className="toggle-elevate data-[state=on]:toggle-elevated" />
 * // Or manually controlling the toggle state
 * <element className="toggle-elevate toggle-elevated" />
 *
 * Elevation systems have to handle many states.
 * - not-hovered, vs. hovered vs. active  (three mutually exclusive states)
 * - toggled or not
 * - focused or not (this is not handled with these utilities)
 *
 * Even without handling focused or not, this is six possible combinations that
 * need to be distinguished from eachother visually.
 */
@layer utilities {

  /* Hide ugly search cancel button in Chrome until we can style it properly */
  input[type="search"]::-webkit-search-cancel-button {
    @apply hidden;
  }

  /* Placeholder styling for contentEditable div */
  [contenteditable][data-placeholder]:empty::before {
    content: attr(data-placeholder);
    color: hsl(var(--muted-foreground));
    pointer-events: none;
  }

  /* .no-default-hover-elevate/no-default-active-elevate is an escape hatch so consumers of
   * buttons/badges can remove the automatic brightness adjustment on interactions
   * and program their own. */
  .no-default-hover-elevate {}

  .no-default-active-elevate {}


  /**
   * Toggleable backgrounds go behind the content. Hoverable/active goes on top.
   * This way they can stack/compound. Both will overlap the parent's borders!
   * So borders will be automatically adjusted both on toggle, and hover/active,
   * and they will be compounded.
   */
  .toggle-elevate::before,
  .toggle-elevate-2::before {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    /*border-radius: inherit;   match rounded corners */
    border-radius: inherit;
    z-index: -1;
    /* sits behind content but above backdrop */
  }

  .toggle-elevate.toggle-elevated::before {
    background-color: var(--elevate-2);
  }

  /* If there's a 1px border, adjust the inset so that it covers that parent's border */
  .border.toggle-elevate::before {
    inset: -1px;
  }

  /* Does not work on elements with overflow:hidden! */
  .hover-elevate:not(.no-default-hover-elevate),
  .active-elevate:not(.no-default-active-elevate),
  .hover-elevate-2:not(.no-default-hover-elevate),
  .active-elevate-2:not(.no-default-active-elevate) {
    position: relative;
    z-index: 0;
  }

  .hover-elevate:not(.no-default-hover-elevate)::after,
  .active-elevate:not(.no-default-active-elevate)::after,
  .hover-elevate-2:not(.no-default-hover-elevate)::after,
  .active-elevate-2:not(.no-default-active-elevate)::after {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    /*border-radius: inherit;   match rounded corners */
    border-radius: inherit;
    z-index: 999;
    /* sits in front of content */
  }

  .hover-elevate:hover:not(.no-default-hover-elevate)::after,
  .active-elevate:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-1);
  }

  .hover-elevate-2:hover:not(.no-default-hover-elevate)::after,
  .active-elevate-2:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-2);
  }

  /* If there's a 1px border, adjust the inset so that it covers that parent's border */
  .border.hover-elevate:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate-2:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate-2:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate:not(.no-hover-interaction-elevate)::after {
    inset: -1px;
  }

  /* Mobile UI improvements - Commit A: Layout móvil PRO */

  /* Ensure minimum touch target size for buttons on mobile */
  @media (max-width: 768px) {
    button {
      min-height: 44px;
      font-size: max(16px, 1rem); /* Prevent font shrinking below 16px */
    }

    /* Readable typography on mobile */
    body {
      font-size: 16px;
      -webkit-text-size-adjust: 100%; /* Prevent text scaling */
    }

    /* Larger text for better readability on mobile */
    p, li, span {
      font-size: max(16px, 1rem);
      line-height: 1.5;
    }

    /* Increase touch area for interactive elements */
    a, input, textarea, select {
      min-height: 44px;
    }

    /* Chess board pieces should be easily tappable */
    [data-testid^="square-"] {
      min-height: 44px;
      min-width: 44px;
    }
  }

  /* Prevent text from becoming too small on any screen */
  * {
    -webkit-tap-highlight-color: rgba(0,0,0,0.1);
    tap-highlight-color: rgba(0,0,0,0.1);
  }

  /* Smooth scrolling for confined scroll areas */
  .overflow-auto, [data-radix-scroll-area-viewport] {
    -webkit-overflow-scrolling: touch;
    overscroll-behavior: contain;
  }

  /* Tablero responsivo con aspect-ratio 1:1 */
  .chess-board-wrapper {
    width: 100%;
    max-width: 88vw;
    margin: 0 auto;
    aspect-ratio: 1 / 1;
  }

  @media (min-width: 1024px) {
    .chess-board-wrapper {
      max-width: 600px;
    }
  }

  /* Sticky controls (botonera pegada al tablero) */
  .move-controls-sticky {
    position: sticky;
    top: 0;
    z-index: 10;
    background: hsl(var(--background));
    padding-block: 0.75rem;
    border-top: 1px solid hsl(var(--border) / 0.5);
    border-bottom: 1px solid hsl(var(--border) / 0.5);
  }

  .dark .move-controls-sticky {
    background: hsl(var(--background));
  }

  /* Chess.com+ Layout: Fixed height with proper scroll */
  html, body, #root {
    height: 100%;
  }

  /* Chess.com+ 3-column grid layout */
  .chesscom-layout {
    display: flex;
    flex-direction: column;
    height: 100vh;
  }

  .chesscom-grid {
    display: grid;
    gap: 0.75rem;
    grid-template-columns: 1fr;
    padding: 0.75rem 0.75rem 5rem; /* Extra bottom padding for mobile dock */
    overflow-y: auto;
    flex: 1;
    min-height: 0;
  }

  @media (min-width: 1024px) {
    .chesscom-grid {
      grid-template-columns: 280px minmax(480px, 720px) 360px;
      gap: 0.75rem;
      padding: 0.75rem; /* Normal padding on desktop */
      overflow-y: auto; /* Allow scroll on desktop too for short screens */
    }
  }

  /* Grid responsive para layout (old - keep for compatibility) */
  .trainer-grid {
    display: grid;
    gap: 1.5rem;
    grid-template-columns: 1fr;
  }

  @media (min-width: 1024px) {
    .trainer-grid {
      grid-template-columns: 1.5fr 1fr;
    }
  }

  /* Panel centrado con max-width */
  .panel-centered {
    max-width: 680px;
    margin: 0 auto;
  }

  /* Anti-scroll: Límite para contenedores de análisis y historial */
  .confined-scroll {
    max-height: 40vh;
    overflow-y: auto;
    overflow-x: hidden;
  }

  /* Scroll interno para move history */
  .move-history-scroll {
    max-height: 40vh;
    overflow-y: auto;
  }

  @media (min-width: 768px) {
    .move-history-scroll {
      max-height: 40vh;
    }
  }
}

