import { useState, useEffect } from "react";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Link } from "wouter";
import { Settings as SettingsIcon, Save, Volume2, VolumeX } from "lucide-react";
import { ThemeToggle } from "@/components/ThemeToggle";
import { useToast } from "@/hooks/use-toast";
import { Label } from "@/components/ui/label";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Slider } from "@/components/ui/slider";
import { useQuery, useMutation } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import type { UserSettings } from "@shared/schema";
import { useVoice } from "@/hooks/use-voice";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";

export default function Settings() {
  const { toast } = useToast();
  const { voiceMode, muted, selectVoice, toggleMute } = useVoice();
  // Fix Pack v5.1: Default to Spanish
  const [coachingStyle, setCoachingStyle] = useState("balanced");
  const [difficulty, setDifficulty] = useState(50);
  const [verbosity, setVerbosity] = useState(50);
  const [language, setLanguage] = useState("spanish");

  // Load settings from API
  const { data: settingsData, isLoading } = useQuery<UserSettings>({
    queryKey: ["/api/settings"],
  });

  // Update local state when settings load from API
  useEffect(() => {
    if (settingsData) {
      setCoachingStyle(settingsData.coachingStyle || "balanced");
      setDifficulty(settingsData.difficulty ?? 50);
      setVerbosity(settingsData.verbosity ?? 50);
      setLanguage(settingsData.language || "spanish"); // Fix Pack v5.1
    }
  }, [settingsData]);

  // Save settings mutation
  const saveSettingsMutation = useMutation({
    mutationFn: async (settings: { coachingStyle: string; difficulty: number; verbosity: number; language: string }) => {
      const res = await apiRequest("PUT", "/api/settings", settings);
      return await res.json();
    },
    onSuccess: () => {
      // Also save to localStorage for offline access
      localStorage.setItem("gm_trainer_settings", JSON.stringify({
        coachingStyle,
        difficulty,
        verbosity,
        language,
      }));

      // Invalidate settings cache
      queryClient.invalidateQueries({ queryKey: ["/api/settings"] });

      toast({
        title: "Settings Saved",
        description: "Your coaching preferences will be applied to future analysis",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Failed to Save Settings",
        description: error.message || "Could not save your preferences",
        variant: "destructive",
      });
    },
  });

  const handleSave = () => {
    saveSettingsMutation.mutate({
      coachingStyle,
      difficulty,
      verbosity,
      language,
    });
  };

  return (
    <div className="min-h-screen bg-background flex flex-col">
      {/* Header */}
      <header className="border-b bg-card">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-16">
            <div className="flex items-center gap-3">
              <SettingsIcon className="h-7 w-7 text-primary" />
              <h1 className="text-2xl font-bold" data-testid="text-settings-title">
                Settings
              </h1>
            </div>

            <div className="flex items-center gap-4">
              <Link href="/">
                <Button variant="outline" data-testid="button-back-to-trainer">
                  Back to Trainer
                </Button>
              </Link>
              <ThemeToggle />
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="flex-1 p-6">
        <div className="max-w-3xl mx-auto space-y-6">
          {/* Coaching Style */}
          <Card className="p-6">
            <h2 className="text-xl font-semibold mb-4">Coaching Style</h2>
            <p className="text-sm text-muted-foreground mb-4">
              Choose how your AI coach analyzes and explains moves
            </p>

            <div className="space-y-4">
              <div>
                <Label htmlFor="coaching-style">Style</Label>
                <Select value={coachingStyle} onValueChange={setCoachingStyle}>
                  <SelectTrigger id="coaching-style" data-testid="select-coaching-style">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="aggressive">Aggressive - Focus on attacking play</SelectItem>
                    <SelectItem value="positional">Positional - Emphasize structure and planning</SelectItem>
                    <SelectItem value="tactical">Tactical - Highlight combinations and tactics</SelectItem>
                    <SelectItem value="balanced">Balanced - Mixed approach</SelectItem>
                    <SelectItem value="defensive">Defensive - Solid and safe play</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>
          </Card>

          {/* Analysis Depth */}
          <Card className="p-6">
            <h2 className="text-xl font-semibold mb-4">Analysis Depth</h2>
            <p className="text-sm text-muted-foreground mb-4">
              Control the complexity of chess analysis and hints
            </p>

            <div className="space-y-4">
              <div>
                <div className="flex items-center justify-between mb-2">
                  <Label>Difficulty Level</Label>
                  <span className="text-sm font-medium">{difficulty}%</span>
                </div>
                <Slider
                  value={[difficulty]}
                  onValueChange={([value]) => setDifficulty(value)}
                  min={0}
                  max={100}
                  step={10}
                  data-testid="slider-difficulty"
                />
                <div className="flex justify-between text-xs text-muted-foreground mt-1">
                  <span>Beginner</span>
                  <span>Advanced</span>
                </div>
              </div>

              <div>
                <div className="flex items-center justify-between mb-2">
                  <Label>Verbosity</Label>
                  <span className="text-sm font-medium">{verbosity}%</span>
                </div>
                <Slider
                  value={[verbosity]}
                  onValueChange={([value]) => setVerbosity(value)}
                  min={0}
                  max={100}
                  step={10}
                  data-testid="slider-verbosity"
                />
                <div className="flex justify-between text-xs text-muted-foreground mt-1">
                  <span>Concise</span>
                  <span>Detailed</span>
                </div>
              </div>
            </div>
          </Card>

          {/* Language */}
          <Card className="p-6">
            <h2 className="text-xl font-semibold mb-4">Language</h2>
            <p className="text-sm text-muted-foreground mb-4">
              Choose your preferred language for voice coaching
            </p>

            <div>
              <Label htmlFor="language">Language</Label>
              <Select value={language} onValueChange={setLanguage}>
                <SelectTrigger id="language" data-testid="select-language">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="english" data-testid="lang-en">English</SelectItem>
                  <SelectItem value="spanish" data-testid="lang-es">Spanish (Español)</SelectItem>
                  <SelectItem value="portuguese" data-testid="lang-pt">Portuguese (Português)</SelectItem>
                  <SelectItem value="hindi" data-testid="lang-hi">Hindi (हिन्दी)</SelectItem>
                  <SelectItem value="french" data-testid="lang-fr">French (Français)</SelectItem>
                  <SelectItem value="german" data-testid="lang-de">German (Deutsch)</SelectItem>
                  <SelectItem value="russian" data-testid="lang-ru">Russian (Русский)</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </Card>

          {/* Voice Settings */}
          <Card className="p-6">
            <h2 className="text-xl font-semibold mb-4">Voice Settings</h2>
            <p className="text-sm text-muted-foreground mb-4">
              Configure your AI coach voice and audio preferences
            </p>

            <div className="space-y-6">
              {/* Mute Toggle */}
              <div className="flex items-center justify-between p-4 rounded-md border bg-card">
                <div className="flex items-center gap-3">
                  {muted ? (
                    <VolumeX className="h-5 w-5 text-muted-foreground" />
                  ) : (
                    <Volume2 className="h-5 w-5 text-primary" />
                  )}
                  <div>
                    <Label className="text-base font-medium">Voice Coach</Label>
                    <p className="text-xs text-muted-foreground">
                      {muted ? "Audio is muted" : "Audio is active"}
                    </p>
                  </div>
                </div>
                <Button
                  variant={muted ? "outline" : "default"}
                  size="lg"
                  onClick={toggleMute}
                  data-testid="button-toggle-mute"
                >
                  {muted ? "Unmute" : "Mute"}
                </Button>
              </div>

              {/* Voice Mode Selection */}
              <div>
                <Label className="text-base mb-3 block">Voice Personality</Label>
                <RadioGroup 
                  value={voiceMode} 
                  onValueChange={(value: 'pro' | 'kids') => selectVoice(value)}
                  className="space-y-3"
                >
                  <div className="flex items-center space-x-3 p-4 rounded-md border hover-elevate" data-testid="radio-voice-pro">
                    <RadioGroupItem value="pro" id="voice-pro" />
                    <Label htmlFor="voice-pro" className="flex-1 cursor-pointer">
                      <div className="font-medium">Leo (Professional)</div>
                      <p className="text-xs text-muted-foreground">
                        Mature, authoritative coaching voice for advanced players
                      </p>
                    </Label>
                  </div>

                  <div className="flex items-center space-x-3 p-4 rounded-md border hover-elevate" data-testid="radio-voice-kids">
                    <RadioGroupItem value="kids" id="voice-kids" />
                    <Label htmlFor="voice-kids" className="flex-1 cursor-pointer">
                      <div className="font-medium">Augusto (Kids)</div>
                      <p className="text-xs text-muted-foreground">
                        Friendly, encouraging voice for young learners
                      </p>
                    </Label>
                  </div>
                </RadioGroup>
              </div>
            </div>
          </Card>

          {/* Save Button */}
          <div className="flex justify-end">
            <Button 
              onClick={handleSave} 
              size="lg" 
              data-testid="button-save-settings"
              disabled={saveSettingsMutation.isPending || isLoading}
            >
              <Save className="w-4 h-4 mr-2" />
              {saveSettingsMutation.isPending ? "Saving..." : "Save Settings"}
            </Button>
          </div>
        </div>
      </main>
    </div>
  );
}

